    public struct MonsterData 
    {
        public string name;
        public string assetsSuffix;
        public string soundSuffix;
        public string trnFile;
        public MonsterAvailability availability;
        public int width;
        public int image;
        public bool hasSpecial;
        public bool hasSpecialSound;
        public int[] frames;
        public int[] rate;
        public int minDunLvl;
        public int maxDunLvl;
        public int level;
        public int hitPointsMinimum;
        public int hitPointsMaximum;
        public _mai_id ai;
        /**
        * @brief Denotes monster's abilities defined in @p monster_flag as bitflags
        * For usage, see @p MonstersData in monstdat.cpp
        */
        public int abilityFlags;
        public int intelligence;
        public int toHit;
        public int animFrameNum;
        public int minDamage;
        public int maxDamage;
        public int toHitSpecial;
        public int animFrameNumSpecial;
        public int minDamageSpecial;
        public int maxDamageSpecial;
        public int armorClass;
        public MonsterClass monsterClass;
        /** Using monster_resistance as bitflags */
        public int resistance;
        /** Using monster_resistance as bitflags */
        public int resistanceHell;
        public int selectionType; // TODO Create enum
        /** Using monster_treasure */
        public int treasure;
        public int exp;

        public MonsterData(string name,
                        string assetsSuffix,
                        string soundSuffix,
                        string trnFile,
                        MonsterAvailability availability,
                        int width,
                        int image,
                        bool hasSpecial,
                        bool hasSpecialSound,
                        int[] frames,
                        int[] rate,
                        int minDunLvl,
                        int maxDunLvl,
                        int level,
                        int hitPointsMinimum,
                        int hitPointsMaximum,
                        _mai_id ai,
                        int abilityFlags,
                        int intelligence,
                        int toHit,
                        int animFrameNum,
                        int minDamage,
                        int maxDamage,
                        int toHitSpecial,
                        int animFrameNumSpecial,
                        int minDamageSpecial,
                        int maxDamageSpecial,
                        int armorClass,
                        MonsterClass monsterClass,
                        int resistance,
                        int resistanceHell,
                        int selectionType,
                        int treasure,
                        int exp)
        {
            this.name = name;
            this.assetsSuffix = assetsSuffix;
            this.soundSuffix = soundSuffix;
            this.trnFile = trnFile;
            this.availability = availability;
            this.width = width;
            this.image = image;
            this.hasSpecial = hasSpecial;
            this.hasSpecialSound = hasSpecialSound;
            this.frames = frames;
            this.rate = rate;
            this.minDunLvl = minDunLvl;
            this.maxDunLvl = maxDunLvl;
            this.level = level;
            this.hitPointsMinimum = hitPointsMinimum;
            this.hitPointsMaximum = hitPointsMaximum;
            this.ai = ai;
            this.abilityFlags = abilityFlags;
            this.intelligence = intelligence;
            this.toHit = toHit;
            this.animFrameNum = animFrameNum;
            this.minDamage = minDamage;
            this.maxDamage = maxDamage;
            this.toHitSpecial = toHitSpecial;
            this.animFrameNumSpecial = animFrameNumSpecial;
            this.minDamageSpecial = minDamageSpecial;
            this.maxDamageSpecial = maxDamageSpecial;
            this.armorClass = armorClass;
            this.monsterClass = monsterClass;
            this.resistance = resistance;
            this.resistanceHell = resistanceHell;
            this.selectionType = selectionType;
            this.treasure = treasure;
            this.exp = exp;
        }
    };

