    public struct UniqueItem 
    {
        public string UIName;
        public unique_base_item UIItemId;
        public int UIMinLvl;
        public int UINumPL;
        public int UIValue;
        public ItemPower[] powers;

        public UniqueItem(string UIName,
                        unique_base_item UIItemId,
                        int UIMinLvl,
                        int UINumPL,
                        int UIValue,
                        ItemPower[] ps)
        {
            this.UIName = UIName;
            this.UIItemId = UIItemId;
            this.UIMinLvl = UIMinLvl;
            this.UINumPL = UINumPL;
            this.UIValue = UIValue;
            this.powers = new ItemPower[6];
            for (int i = 0; i < powers.Length; ++i)
            {
                if (i < ps.Length)
                    powers[i] = ps[i];
                else
                    powers[i] = new ItemPower();
            }
        }
    };

